function togglePasswordVisibility(){let e=document.querySelector(".modal__checkbox");var t=document.getElementById("password"),d=document.getElementById("showPassword");d.checked=!d.checked,d.checked?(e.classList.add("changeColor"),t.type="text"):(e.classList.remove("changeColor"),t.type="password")}function bindModal(e,t,d){e=document.querySelector(e),t=document.querySelector(t),d=document.querySelector(d);const o=document.body;e.addEventListener("click",(e=>{e.preventDefault(),t.style.display="flex",o.classList.add("locked")})),d.addEventListener("click",(()=>{t.style.display="none",o.classList.remove("locked")})),t.addEventListener("click",(e=>{e.target===t&&(t.style.display="none",o.classList.remove("locked"))}))}bindModal(".modal__btn",".modal__wrapper",".modal__close");